 CREATE TABLE IF NOT EXISTS `users` (`id` INTEGER NOT NULL auto_increment , `name` VARCHAR(255), `email` VARCHAR(255) UNIQUE, `password` VARCHAR(255), `isAdmin` TINYINT(1) NOT NULL DEFAULT false, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
 CREATE TABLE IF NOT EXISTS `products` (`id` INTEGER NOT NULL auto_increment , `manufacturer` VARCHAR(255) NOT NULL, `type` VARCHAR(255) NOT NULL, `colour` VARCHAR(255) NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
 CREATE TABLE IF NOT EXISTS `tickets` (`id` INTEGER NOT NULL auto_increment , `userID` INTEGER NOT NULL, `productID` INTEGER NOT NULL, `description` VARCHAR(255), `status` VARCHAR(255) NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;


